import React, { useState } from 'react';
import { addVoiture } from '../services/VoitureService'; // Create this function in your service

const AddVoiture = () => {
  const [marque, setMarque] = useState('');
  const [modele, setModele] = useState('');
  const [matricule, setMatricule] = useState('');
  const [idClient, setIdClient] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    await addVoiture({ marque, modele, matricule, idClient });
    setMarque('');
    setModele('');
    setMatricule('');
    setIdClient('');
    // Optionally, refresh the voiture list or show a success message
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Marque"
        value={marque}
        onChange={(e) => setMarque(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="ModÃ¨le"
        value={modele}
        onChange={(e) => setModele(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Matricule"
        value={matricule}
        onChange={(e) => setMatricule(e.target.value)}
        required
      />
      <input
        type="number"
        placeholder="ID Client"
        value={idClient}
        onChange={(e) => setIdClient(e.target.value)}
        required
      />
      <button type="submit">Ajouter Voiture</button>
    </form>
  );
};

export default AddVoiture;
